import processData from './Data';

const data = processData();
// rearrangeData.js
const rearrangeData = (data) => {
    let rearranged = [];
    let lastItem = {};

    // Helper function to find and remove the next suitable item
    const findNextItem = (items, lastItemName) => {
      for (let i = 0; i < items.length; i++) {
        if (items[i].product !== lastItemName) {
          return items.splice(i, 1)[0]; // Remove and return the item
        }
      }
      return null;
    };

    while (data.length > 0) {
      const nextItem = findNextItem(data, lastItem.product);
      if (nextItem) {
        rearranged.push(nextItem);
        lastItem = nextItem;
      } else {
        // If no suitable item is found, reset the lastItem and try again
        lastItem = {};
      }
    }

    return rearranged;
  };

  export default rearrangeData;


//   export default rearrangeData;


  export const xdata = rearrangeData(data);
