import processData from './Data';

const data = processData();

// rearrangeData.js
const rearrangeData = (data) => {
    let corei4i5Items = [];
    let otherProducts = [];
    let rearranged = new Array(79).fill(null);
    let lastItem = {};

    // Segregate Corei4 and Corei5 items, and other products
    data.forEach(item => {
        if (item.product === 'Corei4' || item.product === 'Corei5') {
            corei4i5Items.push(item);
        } else {
            otherProducts.push(item);
        }
    });

    // Helper function to find and remove the next suitable item
    const findNextItem = (items, lastItemName) => {
        for (let i = 0; i < items.length; i++) {
            if (items[i].product !== lastItemName) {
                return items.splice(i, 1)[0]; // Remove and return the item
            }
        }
        return null;
    };

    // Place Corei4 and Corei5 items in specified ranges
    const placeSpecialItems = () => {
        for (let i = 0; i < 20; i++) {
            if (corei4i5Items.length > 0) {
                rearranged[i] = corei4i5Items.shift();
            }
        }
        for (let i = 40; i < 60; i++) {
            if (corei4i5Items.length > 0) {
                rearranged[i] = corei4i5Items.shift();
            }
        }
    };

    // Fill the remaining spots
    const fillRemaining = () => {
        rearranged.forEach((item, index) => {
            if (!item) {
                const nextItem = findNextItem(otherProducts, lastItem.product);
                if (nextItem) {
                    rearranged[index] = nextItem;
                    lastItem = nextItem;
                }
            } else {
                lastItem = item;
            }
        });
    };

    placeSpecialItems();
    fillRemaining();

    return rearranged;
};

export const xdata = rearrangeData(data);
