import React, { useState, useEffect }  from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Typography, Box, Button } from '@mui/material';
import { xdata} from './Arrange';
import randomColor from 'randomcolor';


const CustomTableCell = (props) => (
  <TableCell align="center" {...props} sx={{ padding: '5px',borderLeft: 1, borderTop: 1, borderRight: 1, border:1 }}/>

);

const CustomNewTableCell = ({ onClick, children, isActive }) => (
  <TableCell 
    align="center" 
    onClick={onClick} 
    sx={{ 
      border: 1, 
      backgroundColor: isActive ? 'lightblue' : null,  // Color change on active state
    }}
  >
    {children}
  </TableCell>
);

const CustomProdTableCell = ({ children, color, ...props }) => (
  <TableCell
    align="center"
    {...props}
    sx={{
      padding: '5px',
      borderLeft: 1,
      borderTop: 1,
      borderRight: 1,
      border: 1,
      backgroundColor: color || 'inherit', // Use productData.color as background color
    }}
  >
    {children}
  </TableCell>
);


const App = () => {
  // Create an array for rows
  const initialRows = new Array(40).fill(null).map((_, index) => ({
    id: index,
    grid: index < 20 ? 'GRID1' : 'GRID3',
    diode: false,
    // diodeActive: false,
    // unmaskActive: false,
    // unmask: index % 2 === 0 ? 'Unmask' : '',
    unmask: false,
    ru: `${index}`,
    //seatUuid: `UUID-${index + 1}`,
    prod: '',
    place: '',
    prod2: '',
    ru2: `${index + 40}`,
    unmask2: false,
    diode2: false,
    grid2: (index+40) < 60  ? 'GRID2' : 'GRID4',
  }));

  const [rows, setRows] = useState(initialRows);



  // const uniqueProducts = new Set(jsonData.map(item => item.product));
  // const colorMap = {};

  // uniqueProducts.forEach(product => {
  //   colorMap[product] = randomColor();
  // });

  // jsonData.map(item => ({
  //   ...item,
  //   color: colorMap[item.product]
  // }));

  const data = React.useMemo(()=> xdata, []);

  // console.log(data);

//   useEffect(() => {
//     // Assuming data is an array of product objects, and each product object has at least a 'product' key
//     const updatedRows = rows.map((row, index) => {
//       const productData = data[index % data.length]; // This will cycle through the products for each row
//       return {
//         ...row,
//         prod: productData.product, // Assign product name to 'prod' (Product1) column
//         prod2: productData.product, // Assign product name to 'prod2' (Product2) column for rows 21-40
//         prodColor: productData.color
//       };
//     });
//     setRows(updatedRows);
// }, []);

  useEffect(() => {
    // Assuming data is an array of product objects, and each product object has at least a 'product' key
    const updatedRows = rows.map((row, index) => {
      const productData = data[index % data.length]; 
      const productData2 = data[(index+40) % data.length]; // This will cycle through the products for each row
      return {
        ...row,
        prod: productData.product, // Assign product name to 'prod' (Product1) column
        prod2: productData2.product, // Assign product name to 'prod2' (Product2) column for rows 21-40
        prodColor: productData.color,
        prodColor2: productData2.color
      };
    });
    setRows(updatedRows);
}, []);

  const toggleDiode = (ru) => {
    setRows(rows.map(row => ({
      ...row, 
      diode: row.ru === ru ? !row.diode : false, 
      diode2: false
    })));
  };

  const toggleUnmask = (ru) => {
    setRows(rows.map(row => ({
      ...row, 
      unmask: row.ru === ru ? !row.unmask: false, // Only turn on the clicked one, reset others
      unmask2: false 
    })));
  };

  const toggleDiode2 = (ru2) => {
    setRows(rows.map(row => ({
      ...row, 
      diode2: row.ru2 === ru2 ? !row.diode2 : false,  
      diode: false
    })));
  };

  const toggleUnmask2 = (ru2) => {
    setRows(rows.map(row => ({
      ...row, 
      unmask2: row.ru2 === ru2 ? !row.unmask2 : false, // Only turn on the clicked one, reset others
      unmask: false
    })));
  };




  return (

    <TableContainer component={Paper}>
      <Typography variant="h6" align="center" style={{ padding: 16 }}>
        TAPE-IN DB VIEW
      </Typography>
      <Table aria-label="custom table" align = "center" sx={{ width: 'auto' }}> 
        <TableHead>
          <TableRow>
            <CustomTableCell>Grid</CustomTableCell>
            <CustomTableCell>Diode</CustomTableCell>
            <CustomTableCell>Unmask</CustomTableCell>
            <CustomTableCell>RU</CustomTableCell>
            <CustomTableCell>Product1</CustomTableCell>
            <CustomTableCell>Place</CustomTableCell>
            <CustomTableCell>Product2</CustomTableCell>
            <CustomTableCell>RU</CustomTableCell>
            <CustomTableCell>Unmask</CustomTableCell>
            <CustomTableCell>Diode</CustomTableCell>
            <CustomTableCell>Grid</CustomTableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row, index) => (
            <>
              {index === 20 && (
                <TableRow key="midhalf">
                  <CustomTableCell colSpan={11}>MIDHALF</CustomTableCell>
                </TableRow>
              )}
              <TableRow key={row.id}>
                {(index === 0 || index === 20) && (
                  <CustomTableCell rowSpan={20}>{row.grid}</CustomTableCell>
                )}

                <CustomNewTableCell isActive={row.diode}
                  onClick={() => toggleDiode(row.ru)}
                >  </CustomNewTableCell>
                <CustomNewTableCell isActive={row.unmask}
                  onClick={() => toggleUnmask(row.ru)}
                >  </CustomNewTableCell>
                <CustomTableCell>{row.ru}</CustomTableCell>
                {/* Duplicate the cells to simulate the layout in the image */}
                <CustomProdTableCell color={row.prodColor}>{row.prod}</CustomProdTableCell>
                <CustomTableCell>{row.place}</CustomTableCell>
                <CustomProdTableCell color={row.prodColor2}>{row.prod2}</CustomProdTableCell>
                <CustomTableCell>{row.ru2}</CustomTableCell>
                <CustomNewTableCell isActive={row.unmask2}
                  onClick={() => toggleUnmask2(row.ru2)}></CustomNewTableCell>
                <CustomNewTableCell isActive={row.diode2}
                  onClick={() => toggleDiode2(row.ru2)}></CustomNewTableCell>
                {(index === 0 || index === 20) && (
                  <CustomTableCell rowSpan={20}>{row.grid2}</CustomTableCell>
                )}


              </TableRow>
            </>
          ))}
        </TableBody>
      </Table>
    </TableContainer>

  );
};

export default App;
