import jsonData from './dataset.json';


// to assign some random color to each product
const getRandomColor = () => {
    const letters = '0123456789ABCDEF';
    let color = '#';
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  };

  // Map over the initial data and add a 'color' key with a random color
  // Adding rcolor as key to the existing json data
  const modifiedData = jsonData.map((item) => ({
    ...item,
    rcolor: getRandomColor(),
  }));

//   console.log(modifiedData);
//   export const xdata = modifiedData

  const processData = () => {
    const processedData = [];

    modifiedData.forEach(item => {
      const repeatTimes = item.repeat || 1;
      for (let i = 0; i < repeatTimes; i++) {
        processedData.push({ ...item }); // Use spread operator to clone the object
      }
    });
    return processedData;
  };

  //  console.log(processData());

export default processData;

