import React, { useState }  from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Typography, Box } from '@mui/material';

const CustomTableCell = (props) => (
  <TableCell align="center" {...props} sx={{ padding: '5px',borderLeft: 1, borderTop: 1, borderRight: 1, borderColor: 'divider' }}/>

);


// const CustomTableCell = styled(TableCell)({
//   // Rotate the header text
//   writingMode: 'vertical-lr',
//   transform: 'rotate(180deg)',
//   textAlign: 'center',
//   height: '100px', // Set a height that fits the rotated text
// });

const App = () => {
  // Create an array for rows
  const initialRows = new Array(40).fill(null).map((_, index) => ({
    id: index,
    grid: index < 20 ? 'GRID1' : 'GRID3',
    //diode: '',
    diodeActive: false,
    unmaskActive: false,
    // unmask: index % 2 === 0 ? 'Unmask' : '',
    //unmask: '',
    ru: `${index}`,
    //seatUuid: `UUID-${index + 1}`,
    prod: '',
    place: '',
    prod2: '',
    ru2: `${index + 40}`,
    unmask2: '',
    diode2: ``,
    grid2: (index+40) < 60  ? 'GRID2' : 'GRID4',
  }));

  const [rows, setRows] = useState(initialRows);

  const toggleDiode = (id) => {
    const updatedRows = rows.map(row => {
      if (row.id === id) {
        return { ...row, diodeActive: !row.diodeActive };
      }
      return row;
    });
    setRows(updatedRows);
  };

  const toggleUnmask = (id) => {
    const updatedRows = rows.map(row => {
      if (row.id === id) {
        return { ...row, unmaskActive: !row.unmaskActive };
      }
      return row;
    });
    setRows(updatedRows);
  };
  return (

    <TableContainer component={Paper}>
      <Typography variant="h6" align="center" style={{ padding: 16 }}>
        TAPE-IN DB VIEW
      </Typography>
      <Table aria-label="custom table" align = "center" sx={{ width: 'auto' }}> 
        <TableHead>
          <TableRow>
            <CustomTableCell>Grid</CustomTableCell>
            <CustomTableCell>Diode</CustomTableCell>
            <CustomTableCell>Unmask</CustomTableCell>
            <CustomTableCell>RU</CustomTableCell>
            <CustomTableCell>Product1</CustomTableCell>
            <CustomTableCell>Place</CustomTableCell>
            <CustomTableCell>Product2</CustomTableCell>
            <CustomTableCell>RU</CustomTableCell>
            <CustomTableCell>Unmask</CustomTableCell>
            <CustomTableCell>Diode</CustomTableCell>
            <CustomTableCell>Grid</CustomTableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row, index) => (
            <>
              {index === 20 && (
                <TableRow key="midhalf">
                  <CustomTableCell colSpan={11}>MIDHALF</CustomTableCell>
                </TableRow>
              )}
              <TableRow key={row.id}>
                {(index === 0 || index === 20) && (
                  <CustomTableCell rowSpan={20}>{row.grid}</CustomTableCell>
                )}

                <CustomTableCell
                > <Button onClick={() => toggleDiode(row.id)}>
                {row.diode ? 'Diode On' : 'Diode Off'}
              </Button> </CustomTableCell>
                <CustomTableCell 
                ></CustomTableCell>
                <CustomTableCell>{row.ru}</CustomTableCell>
                {/* Duplicate the cells to simulate the layout in the image */}
                <CustomTableCell>{row.prod}</CustomTableCell>
                <CustomTableCell>{row.place}</CustomTableCell>
                <CustomTableCell>{row.prod2}</CustomTableCell>
                <CustomTableCell>{row.ru2}</CustomTableCell>
                <CustomTableCell>{row.unmask2}</CustomTableCell>
                <CustomTableCell>{row.diode2}</CustomTableCell>
                {(index === 0 || index === 20) && (
                  <CustomTableCell rowSpan={20}>{row.grid2}</CustomTableCell>
                )}


              </TableRow>
            </>
          ))}
        </TableBody>
      </Table>
    </TableContainer>

  );
};

export default App;
